tinyMCE.addI18n('fr_FR.xoops_quote',{
    _XOOPS_EDITOR_TINYMCE4_QUOTE_TOOLTIP:"Citation",
    _XOOPS_EDITOR_TINYMCE4_QUOTE_SUB:"Collez le texte que vous voulez citer ci-dessous :",
});

tinymce.PluginManager.add('xoops_quote',function(e,url) {function t(){

e.windowManager.open({
	title : '_XOOPS_EDITOR_TINYMCE4_QUOTE_SUB',
	body:{
		type: 'textbox',
		name: 'xoops_quote',
		multiline:!0,
		minWidth:500,
		minHeight:300,
		},

		onSubmit:function(t){
		e.insertContent("<div class='xoopsQuote'>"+t.data.xoops_quote+"</div><br />")
		}}
	)}

e.addButton("xoops_quote",{
	title: 'titre 2',
	tooltip: '_XOOPS_EDITOR_TINYMCE4_QUOTE_TOOLTIP',
	image: url + '/img/quote.png',
	onclick:t
	})
});
