tinyMCE.addI18n('fr_FR.xoops_tagextgal',{
    _XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_TOOLTIP:"Tag eXtGallery",
    _XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_TITLE:"Plugin Tag eXtGallery",
    _XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_LABEL_PHOTO:"Titre de la photo",
    _XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_LABEL_ID:"Id eXtGallery de la photo",

});

tinymce.PluginManager.add("xoops_tagextgal",function(t,url){

t.addButton("xoops_tagextgal",{
	text:"",
	icon:true,
	image:  url + '/img/extgallery.png',
	tooltip:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_TOOLTIP",
	
	onclick:function(){
		t.windowManager.open({
			title:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_TITLE",
			body:[{
				type:"textbox",
				name:"titre_photo",
				label:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_LABEL_PHOTO"},
				{type:"textbox",
				name:"id_photo",
				label:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_LABEL_ID"
			}],
			
	onsubmit:function(e){
		t.insertContent(
			"[gallery title='"+e.data.titre_photo+"']"+e.data.id_photo+"[/gallery]"
			)
		}
		})}
}),

t.addMenuItem("xoops_tagextgal",{
		text:"Tag eXtGallery",
		image:  url + '/img/extgallery.png',		
		tooltip:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_TOOLTIP",
		context:"insert",
		
		onclick:function(){
			t.windowManager.open({
			title:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_TITLE",
			body:[{
				type:"textbox",
				name:"titre_photo",
				label:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_LABEL_PHOTO"},
				{type:"textbox",
				name:"id_photo",
				label:"_XOOPS_EDITOR_TINYMCE4_TAG_EXTGAL_LABEL_ID"
				}],
				
	onsubmit:function(e){
		t.insertContent(
			"[gallery title='"+e.data.titre_photo+"']"+e.data.id_photo+"[/gallery]"
			)
		}
				
			})}})});

